import React, { useEffect, useState } from "react";
import NetInfo from "@react-native-community/netinfo";
import { StyleSheet, Text, View } from "react-native";
import Background from "../components/Background";
import Logo from "../components/Logo";
import Header from "../components/Header";
import Paragraph from "../components/Paragraph";
import Button from "../components/Button";
import { theme } from "../core/theme";

export default function NoInternet(props: any) {
  const [isConnected, setisConnected] = useState(false);
  const [showError, setshowError] = useState(false);
  const { heading, content, buttonLabel, errorText } = props;

  useEffect(() => {
    const unsubscribe = NetInfo.addEventListener((state) => {
      checkStatus(state.isConnected);
    });

    return function cleanup() {
      unsubscribe();
    };
  });

  const handleCheckNet = () => {
    setshowError(false);
    NetInfo.fetch().then((state) => {
      checkStatus(state.isConnected);
      if (!isConnected) {
        setshowError(true);
        setTimeout(function () {
          setshowError(false);
        }, 3000);
      }
    });
  };

  const checkStatus = (status: any) => {
    if (status == true) {
      setisConnected(false);
    } else {
      setisConnected(false);
    }
  };

  return (
    (showError && (
      <View
        style={{
          backgroundColor: theme.colors.error,
          padding: 2,
          alignItems: "center",
          position: "absolute",
          width: "100%",
        }}
      >
        <Text style={{ color: "white", fontSize: 12 }}>{errorText}</Text>
      </View>
    )) || (
      <Background>
        {isConnected ? (
          props.MainComponent
        ) : (
          <View style={styles.background}>
            <Logo />
            <Header>{heading}</Header>
            <Paragraph>{content}</Paragraph>
            <Button mode="contained" onPress={() => handleCheckNet()}>
              {buttonLabel}
            </Button>
          </View>
        )}
      </Background>
    )
  );
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    width: "100%",
  },
});
