import React, { useEffect, useState } from "react";
import NetInfo from "@react-native-community/netinfo";
import { Text, TouchableOpacity, View } from "react-native";

export default function NoInternet(props: any) {
  const [isConnected, setisConnected] = useState(false);
  const [showError, setshowError] = useState(false);
  const {
    heading,
    content,
    buttonLabel,
    errorText,
    themeBg,
    themeBgBottom,
    containerStyle,
    textStyle,
  } = props;

  useEffect(() => {
    const unsubscribe = NetInfo.addEventListener((state) => {
      checkStatus(state.isConnected);
    });

    return function cleanup() {
      unsubscribe();
    };
  });

  const handleCheckNet = () => {
    NetInfo.fetch().then((state) => {
      checkStatus(state.isConnected);
      !isConnected
        ? setshowError(true)
        : setTimeout(function () {
            setshowError(true);
          }, 3000);
    });
  };

  const checkStatus = (status: any) => {
    if (status == true) {
      setisConnected(false);
    } else {
      setisConnected(false);
    }
  };

  return (
    <View style={{ height: "100%" }}>
      {isConnected ? (
        props.MainComponent
      ) : (
        <View style={[{ flex: 1, backgroundColor: "#ccc" }, containerStyle]}>
          {showError && (
            <View
              style={{
                backgroundColor: "red",
                padding: 2,
                alignItems: "center",
                position: "absolute",
                width: "100%",
              }}
            >
              <Text style={{ color: "white", fontSize: 12 }}>{errorText}</Text>
            </View>
          )}
          <View
            style={{
              flex: 1,
              alignItems: "center",
              justifyContent: "center",
              paddingHorizontal: 20,
            }}
          >
            <Text
              style={[
                {
                  color: "white",
                  fontSize: 28,
                  marginBottom: 10,
                  textAlign: "center",
                },
                textStyle,
              ]}
            >
              {heading}
            </Text>
            <Text
              style={[
                {
                  color: "white",
                  fontSize: 18,
                  textAlign: "center",
                  marginBottom: 20,
                },
                textStyle,
              ]}
            >
              {content}
            </Text>
            <TouchableOpacity
              disabled={showError}
              onPress={handleCheckNet}
              style={{
                alignItems: "center",
                justifyContent: "center",
                maxWidth: 120,
                backgroundColor: "yellow",
                width: "100%",
                borderRadius: 8,
                height: 40,
              }}
            >
              <View
                style={[
                  {
                    alignItems: "center",
                    justifyContent: "center",
                    maxWidth: 120,
                    backgroundColor: "yellow",
                    width: "100%",
                    borderRadius: 8,
                    height: 40,
                    position: "absolute",
                    zIndex: 0,
                  },
                  themeBg,
                ]}
              />
              <View
                style={[
                  {
                    alignItems: "center",
                    justifyContent: "center",
                    backgroundColor: "red",
                    borderRadius: 8,
                    position: "absolute",
                    zIndex: -1,
                    bottom: -2.5,
                    top: 1.5,
                    right: 0,
                    left: -2,
                  },
                  themeBgBottom,
                ]}
              />
              <Text>{buttonLabel}</Text>
            </TouchableOpacity>
          </View>
        </View>
      )}
    </View>
  );
}
