import React, { Component } from 'react'
import { Text, View, TouchableOpacity } from 'react-native';
export class NoInternet extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showError: false
        }
    }
    handleCheckNet = () => {
        this.setState({
            showError: true
        }, () => {
            setTimeout(() => {
                this.setState({
                    showError: false
                })
            }, 3000)
        })
    }
    render() {
        const { heading, content, buttonLabel, errorText, themeBg, themeBgBottom, containerStyle, textStyle } = this.props;
        const {showError} = this.state;
        return (
        <View style={[{flex: 1, backgroundColor: '#ccc'}, containerStyle]}>
            {showError && <View style={{backgroundColor: "red", padding: 2, alignItems: "center", position: "absolute", width: "100%"}}>
        <Text style={{color: "white", fontSize: 12}}>{errorText}</Text>
            </View>}
            <View style={{flex: 1, alignItems: "center", justifyContent:"center", paddingHorizontal: 20}}>
                <Text style={[{color: 'white', fontSize: 28, marginBottom: 10, textAlign: "center"}, textStyle]}>{heading}</Text>
                <Text style={[{color: 'white', fontSize: 18, textAlign: "center", marginBottom: 20}, textStyle]}>{content}</Text>
                <TouchableOpacity disabled={showError} onPress={this.handleCheckNet}style={{alignItems: "center", justifyContent:"center", maxWidth: 120, backgroundColor: "yellow", width:"100%", borderRadius: 8, height: 40}}>
                    <View style={[{alignItems: "center", justifyContent:"center", maxWidth: 120, backgroundColor: "yellow", width:"100%", borderRadius: 8, height: 40, position: "absolute", zIndex: 0}, themeBg]} />
                    <View style={[{alignItems: "center", justifyContent:"center", backgroundColor: "red", borderRadius: 8, position: "absolute", zIndex: -1, bottom: -2.5, top: 1.5, right: 0, left: -2}, themeBgBottom]} />
                    <Text>{buttonLabel}</Text>
                </TouchableOpacity>
            </View>
        </View>
        )
    }
}

export default NoInternet;
